import { Template } from 'meteor/templating';

import { Messages } from '../api/messages.js';

import { Meteor } from 'meteor/meteor';

import { Accounts } from 'meteor/accounts-base';

import './login.js';

import './dashboard.js';

import './body.html';


Template.body.onCreated (function bodyOnCreated() {
	Roles.addUserToRoles(Meteor.userId(), 'admin', 'createNewUser');
	Roles.addUserToRoles(Meteor.userId(), 'admin', 'updateOwnProfile');
	Roles.addUserToRoles(Meteor.userId(), 'admin', 'updateOtherProfile');
	Roles.addUserToRoles(Meteor.userId(), 'admin', 'postMessage');
	Roles.addUserToRoles(Meteor.userId(), 'admin', 'deleteOtherMessage');
	Roles.addUserToRoles(Meteor.userId(), 'admin', 'deleteOwnMessage');
	Roles.addUserToRoles(Meteor.userId(), 'admin', 'blockUser');
	Roles.addUserToRoles(Meteor.userId(), 'admin', 'viewUser');
});
